html, body {
  margin: 0;
  padding: 0;
}

menu {
  list-style: none;
  margin: 0;
  padding: 0;
}
menu li {
  background: olive;
  box-sizing: border-box;
  float: left;
  width: 33.3%;
}
menu li a {
  color: #FFF;
  display: block;
  padding: 10px;
  text-decoration: none;
}
menu li a:hover {
  background: #000;
}

p {
  clear: both;
  padding: 20px;
}

